var payee_allocates_info = data.payee_allocates;
for(var i=0; i<payee_allocates_info.length; i++){
    var payee_allocate = data.payee_allocates[i];
    var amount = payee_allocate.amount;
    var ratio = payee_allocate.ratio;
    if (typeof amount !== 'undefined' && amount !== null && amount !== ''){

    } else {        
        amount =  data.amount_payee * ratio * 0.01;
        amount = parseFloat(amount.toFixed(2));
        var amountString = amount.toString();
        if (amountString.indexOf('.') !== -1 && amountString.split('.')[1].length === 1) {
            amountString += '0';
        }
        payee_allocate.amount = amountString;
        data.payee_allocates[i] = payee_allocate;
        }
}
