getPSDEFormItemUpdates.scriptCode:
var order_allocates_info = data.order_allocates;
for(var i = 0; i < order_allocates_info.length; i++){
    var order_allocate = data.order_allocates[i];
    var amount = order_allocate.amount;
    var ratio = order_allocate.ratio;
    if (amount == '' || amount !== null || typeof amount !== 'undefined'){
        if(data.settlement_amount !== '' && data.settlement_amount !== null && typeof data.settlement_amount !== 'undefined'){
            if(ratio !== '' && ratio !== null && typeof ratio !== 'undefined'){
                amount =  data.settlement_amount * ratio * 0.01;
                amount = parseFloat(amount.toFixed(2));
                // 将数值赋值给 order_allocate.amount，保持数值类型
                order_allocate.amount = amount;
                data.order_allocates[i] = order_allocate;
            }
        }
    }
}

if (data.order_allocates.length == 0){
    var order_allocate = {user_id: data.owner, ratio: 100};
    data.order_allocates.push(order_allocate)
}
